def name_transformation(name):
    d= {'А':'A', 'Б':'B', 'В':'V', 'Г':'G',	'Д':'D', 'Е':'E', 'Ё':'E', 'Ж':'ZH', 'З':'Z',
        'И':'I', 'Й':'I', 'К':'K', 'Л':'L',	'М':'M', 'Н':'N', 'О':'O', 'П':'P',	'Р':'R', 
        'С':'S', 'Т':'T', 'У':'U', 'Ф':'F',	'Х':'KH', 'Ц':'TS',	'Ч':'CH', 'Ш':'SH',	'Щ':'SHCH',	
        'Ы':'Y', 'Ъ':'IE', 'Э':'E',	'Ю':'IU', 'Я':'IA', ' ':' ', '-':'-', "'":"'"}
    latin_name = ''
    flag = True
    for i in name.upper():
        if i in d:
            latin_name += d[i]
        else:
            flag = False
            break
    return latin_name if flag is True else f'Не допустимые знаки в ФИО. Пожалуйста, введите, корректные ФИО.'

print(name_transformation('Ишпайкина Надежда Сергеевна'))